import React, { Component } from "react";
import DebtServices from "../../../services/debt.services";

class DebtList extends Component {
  state = {
    debts: [],
    loggedInUser: null
  };

  constructor() {
    super();

    this.services = new DebtServices();
  }

  static getDerivedStateFromProps(oldS, newS) {
    console.log("oldS");
    console.log(oldS);

    if (!oldS.loggedInUser) {
      return oldS;
    }

    this.debtServices.getAllDebts(this.state.loggedInUser._id).then(debts => {
      return {
        ...oldS,
        debts
      };
    });
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.pathname !== this.state.pathname) {
      console.log("prevState.pathname", prevState.pathname);
      console.log("this.props.pathname", this.props.pathname);
      this.animate(0);
    }
  }

  render() {
    // console.log(this.props.debts);
    return (
      <div>
        <div>Hola estas son todas tus deudas</div>;
        {this.state.debts.map(debt => (
          <span>{debt.interest}</span>
        ))}
      </div>
    );
  }
}

export default DebtList;
