import React, { Component } from "react";
import DebtServices from "../../../services/debt.services";
import { Doughnut } from "react-chartjs-2";
import { Line } from "react-chartjs-2";

const totalDebt =0

class DebtList extends Component {
  state = {
    debts: [],
    loggedInUser: null,
    doughnutDebts: [],
    doughnutDebtsLabels: [],
    lineDebts:[],
    lineDebtsLabels: []
  };

  constructor() {
    super();
    this.service = new DebtServices();
  }

  componentDidMount() {
    this.service.getAllDebts(this.props.loggedInUser._id).then(debts => {
      console.log("entra");
      console.log(debts);
      let doughnutDebts = [];
          let doughnutDebtsLabels = [];

         let lineDebts=[]
     let lineDebtsLabels= []
      debts.forEach(debt => {
        console.log("debtremaining");
        console.log(debt.remaining);
        lineDebts.push(debt.history[2018])
        doughnutDebts.push(debt.remaining);
         doughnutDebtsLabels.push(debt.name);
      });
      console.log("totalDebt");
      console.log(doughnutDebts);

      this.setState({
        ...this.state,
        debts,
        doughnutDebts,
        doughnutDebtsLabels
      });
    });
  }

  render() {
    console.log(this.state.doughnutDebts);

const dataLine = {
  labels: ["January", "February", "March", "April", "May", "June", "July"],
  datasets: [
    {
      label: "My First dataset",
      fill: false,
      lineTension: 0.1,
      backgroundColor: "rgba(75,192,192,0.4)",
      borderColor: "rgba(75,192,192,1)",
      borderCapStyle: "butt",
      borderDash: [],
      borderDashOffset: 0.0,
      borderJoinStyle: "miter",
      pointBorderColor: "rgba(75,192,192,1)",
      pointBackgroundColor: "#fff",
      pointBorderWidth: 1,
      pointHoverRadius: 5,
      pointHoverBackgroundColor: "rgba(75,192,192,1)",
      pointHoverBorderColor: "rgba(220,220,220,1)",
      pointHoverBorderWidth: 2,
      pointRadius: 1,
      pointHitRadius: 10,
      data: [65, 59, 80, 81, 56, 55, 40]
    }
  ]
};

    const dataChartDoughnut = {
      labels: this.state.doughnutDebtsLabels,
      datasets: [
        {
          data: this.state.doughnutDebts,
          backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#009018","#00c5ff"],
          hoverBackgroundColor: ["#d4395a", "#3791ce", "#dbaa31", "#18872a","#0ca5d2"]
        }
      ]
    };
    return (
      <div>
        <div>Hola estas son todas tus deudas</div>;
        <Doughnut data={dataChartDoughnut}></Doughnut>
        <Line data={dataLine}></Line>


        {this.state.debts.map((debt, idx) => (
          <span key={idx}>{debt.interestRate}</span>
        ))}
      </div>
    );
  }
}

export default DebtList;
