import React, { Component } from "react";
import DebtServices from "../../../services/debt.services";
import { Doughnut } from "react-chartjs-2";

const totalDebt =0

class DebtList extends Component {
  state = {
    debts: [],
    loggedInUser: null,
    doughnutDebts: [],
    doughnutDebts: []
  };

  constructor() {
    super();
    this.service = new DebtServices();
  }

  componentDidMount() {
    this.service.getAllDebts(this.props.loggedInUser._id).then(debts => {
      console.log("entra");
      console.log(debts);
      let doughnutDebts = [];
      debts.forEach(debt => {
        console.log("debtremaining");
        console.log(debt.remaining);
        doughnutDebts.push(debt.remaining);
      });
      console.log("totalDebt");
      console.log(doughnutDebts);

      this.setState({
        ...this.state,
        debts,
        doughnutDebts
      });
    });
  }

  render() {
    console.log(this.state.doughnutDebts);

    const dataChart = {
      labels: ["Red", "Green", "Yellow"],
      datasets: [
        {
          data: this.state.doughnutDebts,
          backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
          hoverBackgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"]
        }
      ]
    };
    return (
      <div>
        <div>Hola estas son todas tus deudas</div>;
        <Doughnut data={dataChart}></Doughnut>
        {this.state.debts.map((debt, idx) => (
          <span key={idx}>{debt.interestRate}</span>
        ))}
      </div>
    );
  }
}

export default DebtList;
