import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

import "./App.css";

import { Switch, Route, Redirect } from "react-router-dom";

import CoastersList from "./components/pages/coasterList/CoasterList";
import CoasterDetails from "./components/pages/coasterDetails/CoasterDetails";
import NavBar from "./components/ui/NavBar";

import Signup from "./components/pages/auth/signup/Signup";
import Profile from "./components/pages/profile/Profile";
import Login from "./components/pages/auth/login/Login";

import AuthServices from "./services/auth.services";
import { Doughnut } from "react-chartjs-2";
import { Line } from "react-chartjs-2";


 let json = {
   // "2018": {
   //         "January": 1500,
   //         "February": 1500,
   //         "March": 1400,
   //         "April": 1200,
   //         "May": 1200,
   //         "June": 1100,
   //         "July": 1400,
   //         "August": 1300,
   //         "September": 1100,
   //         "November": 900,
   //         "December": 700
   //         },
   "2019": {
     January: 1500,
     February: 1500,
     March: 1400,
     April: 1200,
     May: 1200,
     June: 1100,
     July: 1400,
     August: 1300,
     September: 1100,
     November: 900,
     December: 700
   }
 };

 let arrData = [json];

 let months = Object.keys(json["2019"]);
 let monthlyDebts = Object.values(json["2019"]);
 console.log(months);
 console.log(monthlyDebts);

class App extends Component {
  constructor() {
    super();
    this.state = {
      loggedInUser: false
    };
    this.services = new AuthServices();
  }

  componentDidUpdate = (prevProps, prevState) =>
    console.log("El estado de App se ha actualizado:", this.state);
  componentDidMount = () => this.fetchUser();

  setTheUser = userObj =>
    this.setState({
      loggedInUser: userObj
    });
  fetchUser = () => {
    this.services
      .loggedin()
      .then(theUser =>
        this.setState({
          loggedInUser: theUser
        })
      )
      .catch(() =>
        this.setState({
          loggedInUser: false
        })
      );
  };

  render() {
    return (
      <>
        <NavBar
          setTheUser={this.setTheUser}
          loggedInUser={this.state.loggedInUser}
        />

        <Doughnut data={data}></Doughnut>
        <Line data={data2}></Line>

        <Switch>
          <Route
            exact
            path="/"
            render={() => (
              <CoastersList loggedInUser={this.state.loggedInUser} />
            )}
          />
          <Route
            path="/detalles/:id"
            render={props => <CoasterDetails {...props} />}
          />
          <Route
            path="/signup"
            render={() => <Signup setTheUser={this.setTheUser} />}
          />
          <Route
            path="/login"
            render={props => <Login setTheUser={this.setTheUser} {...props} />}
          />
          <Route
            path="/profile"
            render={() =>
              this.state.loggedInUser ? (
                <Profile loggedInUser={this.state.loggedInUser} />
              ) : (
                <Redirect to="/" />
              )
            }
          />
        </Switch>
      </>
    );
  }
}

export default App;
