import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

import "./App.css";

import { Switch, Route, Redirect } from "react-router-dom";

import CoastersList from "./components/pages/coasterList/CoasterList";
import CoasterDetails from "./components/pages/coasterDetails/CoasterDetails";
import NavBar from "./components/ui/NavBar";

import Signup from "./components/pages/auth/signup/Signup";
import Profile from "./components/pages/profile/Profile";
import Login from "./components/pages/auth/login/Login";

import AuthServices from "./services/auth.services";
import { Doughnut } from "react-chartjs-2";
import { Line } from "react-chartjs-2";

 let json = {
   // "2018": {
   //         "January": 1500,
   //         "February": 1500,
   //         "March": 1400,
   //         "April": 1200,
   //         "May": 1200,
   //         "June": 1100,
   //         "July": 1400,
   //         "August": 1300,
   //         "September": 1100,
   //         "November": 900,
   //         "December": 700
   //         },
   "2019": {
     January: 1500,
     February: 1500,
     March: 1400,
     April: 1200,
     May: 1200,
     June: 1100,
     July: 1400,
     August: 1300,
     September: 1100,
     November: 1300,
     December: 1200
   }
 };
 let arrData = [json];

 let months = Object.keys(json["2019"]);
 let monthlyDebts = Object.values(json["2019"]);
 console.log(months);
 console.log(monthlyDebts);

 const data2 = {
   labels: months,
   datasets: [
     {
       label: "My First dataset",
       fill: false,
       lineTension: 0.1,
       backgroundColor: "rgba(75,192,192,0.4)",
       borderColor: "rgba(75,192,192,1)",
       borderCapStyle: "butt",
       borderDash: [],
       borderDashOffset: 0.0,
       borderJoinStyle: "miter",
       pointBorderColor: "rgba(75,192,192,1)",
       pointBackgroundColor: "#fff",
       pointBorderWidth: 1,
       pointHoverRadius: 5,
       pointHoverBackgroundColor: "rgba(75,192,192,1)",
       pointHoverBorderColor: "rgba(220,220,220,1)",
       pointHoverBorderWidth: 2,
       pointRadius: 1,
       pointHitRadius: 10,
       data: monthlyDebts
     }
   ],
   options: {
     scales: {
       yAxes: [
         {
           ticks: {
             min: 0
           }
         }
       ]
     }
   }
 };
 const data = {
   labels: ["Red", "Green", "Yellow"],
   datasets: [
     {
       data: [300, 50, 100],
       backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
       hoverBackgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"]
     }
   ]
 };


class App extends Component {
  constructor() {
    super();
    this.state = {
      loggedInUser: false
    };
    this.services = new AuthServices();
  }

  componentDidUpdate = (prevProps, prevState) =>
    console.log("El estado de App se ha actualizado:", this.state);
  componentDidMount = () => this.fetchUser();

  setTheUser = userObj =>
    this.setState({
      loggedInUser: userObj
    });
  fetchUser = () => {
    this.services
      .loggedin()
      .then(theUser =>
        this.setState({
          loggedInUser: theUser
        })
      )
      .catch(() =>
        this.setState({
          loggedInUser: false
        })
      );
  };

  render() {
    return (
      <>
        <NavBar
          setTheUser={this.setTheUser}
          loggedInUser={this.state.loggedInUser}
        />

        <Doughnut data={data}></Doughnut>
        <Line data={data2}></Line>

        <Switch>
          <Route
            exact
            path="/"
            render={() => (
              <CoastersList loggedInUser={this.state.loggedInUser} />
            )}
          />
          <Route
            path="/detalles/:id"
            render={props => <CoasterDetails {...props} />}
          />
          <Route
            path="/signup"
            render={() => <Signup setTheUser={this.setTheUser} />}
          />
          <Route
            path="/login"
            render={props => <Login setTheUser={this.setTheUser} {...props} />}
          />
          <Route
            path="/profile"
            render={() =>
              this.state.loggedInUser ? (
                <Profile loggedInUser={this.state.loggedInUser} />
              ) : (
                <Redirect to="/" />
              )
            }
          />
        </Switch>
      </>
    );
  }
}

export default App;
